import 'package:flutter/material.dart';
import 'package:myhw/comp/appBar.dart';
import 'package:myhw/comp/slidImag.dart';

class toolImage extends StatelessWidget {
  const toolImage({super.key});

  @override
  Widget build(BuildContext context) {
    return  Stack(
      alignment: 
      AlignmentDirectional.bottomCenter,
      children: [
        Stack(
          children: [
            Image.network('data:image/jpeg;base64,',
            height: 400,
            width: 450,
            fit: BoxFit.fill,
